import React from "react";
import './index.css';
import { Line } from 'react-chartjs-2';

const getLabels = (currency) => {
  return ['1', '2', '3', '4', '5', '6'];
}
const getData = (currency) => {
  return ['1', '2', '3', '4', '5', '6'];
}

const genData = () => (
  {
  labels: getLabels(),
  datasets: [
    {
      label: 'Price',
      data: getData(),
      fill: false,
      backgroundColor: 'rgb(255, 99, 132)',
      borderColor: 'rgba(255, 99, 132, 0.2)',
    },
  ],
});

const options = {
  scales: {
    yAxes: [
      {
        ticks: {
          beginAtZero: true,
        },
      },
    ],
  },
};

function App() {
  const[time, setTime] = React.useState();

  const [items1, setItems1] = React.useState([]);
  const [val1, setVal1] = React.useState();
  const [chart1, setChart1] = React.useState(genData(""));

  const [items2, setItems2] = React.useState([]);
  const [val2, setVal2] = React.useState();
  const [chart2, setChart2] = React.useState(genData(""));

  const [items3, setItems3] = React.useState([]);
  const [val3, setVal3] = React.useState();
  const [chart3, setChart3] = React.useState(genData(""));

  React.useEffect(() => {
    let unmounted = false;
    async function getCurrencies() {
      const url = "	https://rest.coinapi.io/v1/exchanges";
      const response = await fetch(url, 
      {
        method: "GET",
        headers: {'X-CoinAPI-Key': '78B1F171-DE93-4077-99E6-00131F1178E9'}
      });
      const body = await response.json();
      if (!unmounted) {
        setItems1(
          body.map(({ name }) => ({ label1: name, value1: name }))
        );
      }
    }
    getCurrencies();
    setChart1(genData());
    return () => {
      unmounted = true;
    };
  }, []);

  React.useEffect(() => {
    let unmounted = false;
    async function getCurrencies() {
      const url = "	https://rest.coinapi.io/v1/exchanges";
      const response = await fetch(url, 
      {
        method: "GET",
        headers: {'X-CoinAPI-Key': '78B1F171-DE93-4077-99E6-00131F1178E9'}
      });
      const body = await response.json();
      if (!unmounted) {
        setItems2(
          body.map(({ name }) => ({ label: name, value: name }))
        );
      }
    }
    getCurrencies();
    setChart2(genData());
    return () => {
      unmounted = true;
    };
  }, []);

  React.useEffect(() => {
    let unmounted = false;
    async function getCurrencies() {
      const url = "	https://rest.coinapi.io/v1/exchanges";
      const response = await fetch(url, 
      {
        method: "GET",
        headers: {'X-CoinAPI-Key': '78B1F171-DE93-4077-99E6-00131F1178E9'}
      });
      const body = await response.json();
      if (!unmounted) {
        setItems3(
          body.map(({ name }) => ({ label: name, value: name }))
        );
      }
    }
    getCurrencies();
    setChart3(genData());
    return () => {
      unmounted = true;
    };
  }, []);

  return (
    <section className="grid-container mainChart">
      <div className="grid-item logo">Logo</div>
      <div id="lgChart" className="grid-item lgChart"><p>{time}</p></div>
      <div className="grid-item timeFrames">
        <button onClick={(e) => setTime(e.target.value)} type="button" className="btn btn-secondary" value="1D">1D</button>
        <button onClick={(e) => setTime(e.target.value)} type="button" className="btn btn-secondary" value="1W">1W</button>
        <button onClick={(e) => setTime(e.target.value)} type="button" className="btn btn-secondary" value="1M">1M</button>
        <button onClick={(e) => setTime(e.target.value)} type="button" className="btn btn-secondary" value="3M">3M</button>
        <button onClick={(e) => setTime(e.target.value)} type="button" className="btn btn-secondary" value="6M">6M</button>
        <button onClick={(e) => setTime(e.target.value)} type="button" className="btn btn-secondary" value="1Y">1Y</button>
      </div>
      <div className="grid-item options">
        <button type="button" className="btn btn-primary">MarketCap</button>
        <button type="button" className="btn btn-primary">Price</button>
        <button type="button" className="btn btn-primary">Overlay</button>
        <button type="button" className="btn btn-primary">Volume</button>
      </div>
      <div id="smChart1" className="grid-item smChart1">
        <p>{val1}</p>
        <Line data={chart1} options={options} />
        <select value={val1} onChange={(e) => setVal1(e.currentTarget.value)}>
          {items1.map(({ label1, value1 }) => (
            <option key={value1} value={value1}>{label1}</option>
          ))}
        </select>
      </div>
      <div id="smChart2" className="grid-item smChart2">
        <p>{val2}</p>
        <Line data={chart2} options={options} />
        <select value={val2} onChange={(e) => setVal2(e.currentTarget.value)}>
          {items2.map(({ label2, value2 }) => (
            <option key={value2} value={value2}>{label2}</option>
          ))}
        </select>
      </div>
      <div id="smChart3" className="grid-item smChart3">
        <p>{val3}</p>
        <Line data={chart3} options={options} />
        <select value={val3} onChange={(e) => setVal3(e.currentTarget.value)}>
          {items3.map(({ label3, value3 }) => (
            <option key={value3} value={value3}>{label3}</option>
          ))}
        </select>
      </div>
    </section>
  );
}

export default App;
