import './index.css';

function SmChartOne() {

    const url = 'https://restcountries.eu/rest/v2/all';

    let getDropdownOptions = (url) => {
      
      fetch(url).then(response => response.json()).then (data => {
        let div1 = document.createElement("div");
        let select1 = document.createElement("select");
    
        for (let i = 0; i < data.length; i++) {  
            let option = document.createElement("option");
            option.value = data[i].name;
            option.text = data[i].name.toUpperCase();
            select1.appendChild(option);
        }
    
        let label1 = document.createElement("label");
        label1.innerHTML = "Choose Cryptocurrency:"
        label1.htmlFor = "chartOneForm";   
    
        div1.appendChild(label1).appendChild(select1);
        document.getElementById('chartOneDropdown').replaceWith(div1);
        })
        .catch(error => {
            console.log('Request failed', error);
      })
    }

    getDropdownOptions(url);
  
    return (
    <section class="grid-container charts1">
        <div class="grid-item chart1">Chart One</div>
        <div id="chartOneDropdown"></div>
    </section>
  );
}

export default SmChartOne;
