import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import SmChartOne from './SmChart';
import 'bootstrap/dist/css/bootstrap.min.css';

ReactDOM.render(
  <React.StrictMode>
    <SmChartOne />
  </React.StrictMode>,
  document.getElementById('smChart1')
);

ReactDOM.render(
  <React.StrictMode>
    <SmChartOne />
  </React.StrictMode>,
  document.getElementById('smChart2')
);

ReactDOM.render(
  <React.StrictMode>
    <SmChartOne />
  </React.StrictMode>,
  document.getElementById('smChart3')
);

document.getElementsByTagName('button').addEventListener("click", function() {
    console.log(TimeFrames());
    document.getElementById('lgChart').innerHTML = TimeFrames();
    ResetButtons();
  });

function ResetButtons(optionNumber) {
let timeframes = document.getElementsByClass('timeFrames');
for(let i = 0; i < timeframes.length; i++){
    if(timeframes[i].checked && timeframes[i] !== optionNumber){
        timeframes.checked = false;
    }
}
}
function TimeFrames() {
    let timeframes = document.getElementsByClassName('timeFrames');
    let time_value;
    for(let i = 0; i < timeframes.length; i++){
        if(timeframes[i].checked){
            time_value = timeframes[i].value;
        }
        return time_value;
    }
    }
