import React from "react";
import './index.css';
import {Chart} from 'chart.js';

const getLabelArray = (tf) => {
  if(tf === "1D")
    return ['1', '2', '3', '4', '5', '6'];
  if(tf === "1W")
    return ['w', 'w', 'w', 'w', '5', '6'];
  else 
    return ['e ', 'e ', 'e ', 'e ', 'e ', 'e ']
}

const getData = () => {
  return ['1', '2', '3', '4', '5', '6'];
}
const chartConfig = {
  type: 'line',
  data: {
    labels: getLabelArray(),
    datasets: {
      data: getData(),
      fill: false,
      backgroundColor: 'rgb(255, 99, 132)',
      borderColor: 'rgba(255, 99, 132, 0.2)',}
  },
  options: {
    scales: {
      yAxes: [
        {
          ticks: {
            beginAtZero: true,
          },
        },
      ],
    },
  }
};

function App() {
  const[time, setTime] = React.useState();

  const [items1, setItems1] = React.useState([]);
  const [val1, setVal1] = React.useState();
  const chart1 = React.useRef(null);
  const [chartInstance1, setChartInstance1] = React.useState(null);

  const [items2, setItems2] = React.useState([]);
  const [val2, setVal2] = React.useState();
  const chart2 = React.useRef(null);
  const [chartInstance2, setChartInstance2] = React.useState(null);

  const [items3, setItems3] = React.useState([]);
  const [val3, setVal3] = React.useState();
  const chart3 = React.useRef(null);
  const [chartInstance3, setChartInstance3] = React.useState(null);

  React.useEffect(() => {
    if (chart1 && chart1.current) {
      const newChartInstance = new Chart(chart1.current, chartConfig);
      setChartInstance1(newChartInstance);
    }
  }, [chart1]);

  React.useEffect(() => {
    let unmounted = false;
    async function getCurrencies() {
      const url = 'https://restcountries.eu/rest/v2/all'
      //"	https://rest.coinapi.io/v1/exchanges";
      const response = await fetch(url, 
      {
        method: "GET",
        //headers: {'X-CoinAPI-Key': '78B1F171-DE93-4077-99E6-00131F1178E9'}
      });
      const body = await response.json();
      if (!unmounted) {
        setItems1(
          body.map(({ name }) => ({ label: name, value: name }))
        );
      }
    }
    getCurrencies();
    return () => {
      unmounted = true;
    };
  }, []);

  React.useEffect(() => {
    if (chart2 && chart2.current) {
      const newChartInstance = new Chart(chart2.current, chartConfig);
      setChartInstance2(newChartInstance);
    }
  }, [chart2]);

  React.useEffect(() => {
    let unmounted = false;
    async function getCurrencies() {
      const url = 'https://restcountries.eu/rest/v2/all';
      //"	https://rest.coinapi.io/v1/exchanges";
      const response = await fetch(url, 
      {
        method: "GET",
        //headers: {'X-CoinAPI-Key': '78B1F171-DE93-4077-99E6-00131F1178E9'}
      });
      const body = await response.json();
      if (!unmounted) {
        setItems2(
          body.map(({ name }) => ({ label: name, value: name }))
        );
      }
    }
    getCurrencies();
    return () => {
      unmounted = true;
    };
  }, []);

  React.useEffect(() => {
    if (chart3 && chart3.current) {
      const newChartInstance = new Chart(chart3.current, chartConfig);
      setChartInstance3(newChartInstance);
    }
  }, [chart3]);

  React.useEffect(() => {
    let unmounted = false;
    async function getCurrencies() {
      const url = 'https://restcountries.eu/rest/v2/all';
      //"	https://rest.coinapi.io/v1/exchanges";
      const response = await fetch(url, 
      {
        method: "GET",
        //headers: {'X-CoinAPI-Key': '78B1F171-DE93-4077-99E6-00131F1178E9'}
      });
      const body = await response.json();
      if (!unmounted) {
        setItems3(
          body.map(({ name }) => ({ label: name, value: name }))
        );
      }
    }
    getCurrencies();
    return () => {
      unmounted = true;
    };
  }, []);

  return (
    <section className="grid-container mainChart">
      <div className="grid-item logo">Logo</div>
      <div id="lgChart" className="grid-item lgChart"><p>{time}</p></div>
      <div className="grid-item timeFrames">
        <button onClick={(e) => setTime(e.currentTarget.value)} type="button" className="btn btn-secondary" value="1D">1D</button>
        <button onClick={(e) => setTime(e.currentTarget.value)} type="button" className="btn btn-secondary" value="1W">1W</button>
        <button onClick={(e) => setTime(e.currentTarget.value)} type="button" className="btn btn-secondary" value="1M">1M</button>
        <button onClick={(e) => setTime(e.currentTarget.value)} type="button" className="btn btn-secondary" value="3M">3M</button>
        <button onClick={(e) => setTime(e.currentTarget.value)} type="button" className="btn btn-secondary" value="6M">6M</button>
        <button onClick={(e) => setTime(e.currentTarget.value)} type="button" className="btn btn-secondary" value="1Y">1Y</button>
      </div>
      <div className="grid-item options">
        <button type="button" className="btn btn-primary">MarketCap</button>
        <button type="button" className="btn btn-primary">Price</button>
        <button type="button" className="btn btn-primary">Overlay</button>
        <button type="button" className="btn btn-primary">Volume</button>
      </div>
      <div id="smChart1" className="grid-item smChart1">
        <p>{val1}</p>
        <canvas ref={chart1} value={chartInstance1}/>
        <select value={val1} onChange={(e) => setVal1(e.currentTarget.value)}>
          {items1.map(({ label, value1 }) => (
            <option key={value1} value={value1}>{label}</option>
          ))}
        </select>
      </div>
      <div id="smChart2" className="grid-item smChart2">
        <p>{val2}</p>
        <canvas ref={chart2} value={chartInstance2} />
        <select value={val2} onChange={(e) => setVal2(e.currentTarget.value)}>
          {items2.map(({ label, value2 }) => (
            <option key={value2} value={value2}>{label}</option>
          ))}
        </select>
      </div>
      <div id="smChart3" className="grid-item smChart3">
        <p>{val3}</p>
        <canvas ref={chart3} value={chartInstance3} />
        <select value={val3} onChange={(e) => setVal3(e.currentTarget.value)}>
          {items3.map(({ label, value3 }) => (
            <option key={value3} value={value3}>{label}</option>
          ))}
        </select>
      </div>
    </section>
  );
}

export default App;
